mapper.update:
UPDATE profile
SET password=#{password}
WHERE email=#{email}

UPDATE profile
SET phoneNumber=#{phoneNumber}, nickName=#{nickName}, profileDefaultPic=#{profileDefaultPic}
WHERE email=#{email}

UPDATE profile
SET profileDefaultPic=#{profileDefaultPic}
WHERE email=#{email}

UPDATE profile
SET phoneNumber=#{phoneNumber}
WHERE email=#{email}

UPDATE profile
SET nickName=#{nickName}
WHERE email=#{email}

mapper.select:
SELECT ProfileID
FROM profile
WHERE email=#{email}
